import { Flex, Spinner, Box } from '@chakra-ui/react';
import Pagination from '@choc-ui/paginator';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { CardGrid } from './CardGrid';








const Main = ({searchTerm}) => {
    const tmdbKey = "5f4cf67aa6b81c56e16cff977537cf17";
    const basicURL= "https://api.themoviedb.org/3/search/";
    const [totalPages,setTotalPages]=useState(false)
    const [data,setData]= useState([])
    const [pageValue,setPageValue]= useState(1)
    const getPageValue=(value)=>setPageValue(value)

    useEffect(() => {
        const fetchData= async ()=>{ 
        const axiosRequest= await axios.get("https://api.themoviedb.org/3/trending/all/day?api_key=5f4cf67aa6b81c56e16cff977537cf17")
        setData(axiosRequest.data.results)
        setTotalPages(await axiosRequest.data.total_pages)
        console.log(totalPages)}
        fetchData()     
    }, [])

    useEffect(() => {
        const fetchData= async ()=>{ 
            try{ 
            console.log(searchTerm)
            const axiosRequest= await axios.get(`${basicURL}movie?page=${pageValue}&query=${searchTerm}&api_key=${tmdbKey}`)
        console.log(axiosRequest)
        setTotalPages(await axiosRequest.data.total_pages)
        setData(await axiosRequest.data.results)}
            catch (error){ console.log(error)}
       }  
        fetchData() ;
        setTotalPages(totalPages)
    }, [searchTerm, pageValue])

    const handlePagechange=(value)=>{setPageValue(value); window.scrollTo(0, 0)}

    useEffect(()=>{console.log(totalPages)},[totalPages])

  return (
        <Box>
            <CardGrid data={data}/>
            <Flex
  w="full"
  bg={"gray.400"}
  _dark={{
    bg: "gray.600",
  }}
  p={50}
  alignItems="center"
  justifyContent="center"
>
  <Pagination
    defaultCurrent={1}
    total={14}
    onChange={handlePagechange}  
    paginationProps={{
      display: "flex",
    }}
  />
</Flex>;

        </Box>

  )
}


export default Main
