import { HStack, Image, Box, Text, Flex, Spacer } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";
import popcorn from "./Popcorn.png";

const Home = () => {
  const [movieData, setMovieData] = useState("");
  const [tvData, setTvData] = useState("");

  useEffect(() => {
    const getTrendingMovies = async () => {
      const trendingMovies = await getTrending("movie");
      setMovieData(trendingMovies?.slice(0, 9));
    };
    const getTrendingTV = async () => {
      const trendingTV = await getTrending("tv");
      setTvData(trendingTV?.slice(0, 9));
    };
    getTrendingMovies();
    getTrendingTV();
  }, []);

  return (
    <Flex direction={"row"} pl="4rem" h={"35rem"} w={"100%"} margin={"3rem"}>
      {movieData ? (
        <Carousel autoPlay={false}>
          {movieData?.map((item, i) => (
            <HStack h={"35rem"}>
              <Image
                style={{ marginTop: "0px" }}
                w={"20rem"}
                h={"25rem"}
                borderTopRadius="base"
                src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
                onError={(e) => (e.currentTarget.src = popcorn)}
              ></Image>
              <Text size={"2xl"} fontWeight="semibold" w={"30rem"}>
                {item.overview}
              </Text>
            </HStack>
          ))}
        </Carousel>
      ) : (
        <div></div>
      )}
      {tvData ? (
        <Carousel autoPlay={false}>
          {tvData.map((item, i) => (
            <HStack h={"35rem"}>
              <Image
                style={{ marginTop: "0px" }}
                w={"40rem"}
                h={"25rem"}
                borderTopRadius="base"
                src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
                onError={(e) => (e.currentTarget.src = popcorn)}
              ></Image>
              <Text size={"2xl"} fontWeight="semibold">
                {item.overview}
              </Text>
            </HStack>
          ))}
        </Carousel>
      ) : (
        <div></div>
      )}
    </Flex>
  );
};

export default Home;
