import { StarIcon } from '@chakra-ui/icons'
import { Image, VStack, Text, HStack, Flex, Center } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {
    console.log(item)
  return (

    <VStack 
        data-group position={'relative'} 
        overflow={'hidden'} 
        borderWidth={'10px'} 
        borderRadius={"2xl"} 
        bg={"#8861c2"}   
        alignContent={'center'}>
            <Flex display={'inline-flex'} justifyItems='flex-end' direction={'row'} position={'absolute'} alignItems={'center'} >
                    <Text>CHAU</Text>
                    <Text color={'yellow'} >HOLA</Text>
                    <StarIcon boxSize={'6'} color={"gold"}/>

            </Flex>
            <Image style={{marginTop:"0px"}}  w={'100%'} h={'100%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}></Image>
            <Text 
            bg={"rgba(255, 255, 255, 0.832)"}
            color="black"
            transform={'auto'}
            translateY="100%" 
            position={'absolute'} 
            bottom='0' 
            left='0' 
            right='0' 
            maxHeight={'100%'}
            _groupHover={{transform: "translateY(0%)", transition: "transform 0.3s ease-in-out"
            }}
            >{item.overview}
            </Text>
            <Center isTruncated
            px={'1rem'}

           textOverflow={'ellipsis'} 
            overflow="hidden" 
            height={'4.5em'} 
            fontSize="lg" 
            fontWeight={'bold'}
            >{item.original_title||item.original_name}
            </Center> 
    </VStack>

  )
}
