import { Text } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";


const Home=()=>{
const [data,setData]=useState('')


useEffect(() => {
    const getData=async()=>{
    const trendingMovies= await getTrending()
    return trendingMovies? setData(trendingMovies): trendingMovies}
    getData() 
}, [data])



return(
<>
    <Text>HOLA</Text>
    <div style={{ width: 600, height: 400, margin: "0 auto" }}>
    <Carousel >
        {data?.map((item, i) => (
        <img
            draggable="false"
            src={`./${item}.jpg`}
            key={i}
            width="100%"
            alt=""
        />
        ))}
    </Carousel>
    </div>
</>)


}



export default Home
