import React from "react";
import { Card } from "./Card";
import { SimpleGrid, Box, GridItem } from "@chakra-ui/react";

export const CardGrid = ({ data }) => {
  return (
    <Box py={["1rem", "50px"]} mx={["0.5rem", "50px"]}>
      <SimpleGrid columns={[3, 3, 5]} spacing="1rem">
        {data.map((item) => (
          <GridItem h={"100%"}>
            {" "}
            <Card item={item} />
          </GridItem>
        ))}
      </SimpleGrid>
    </Box>
  );
};
