import { StarIcon } from '@chakra-ui/icons'
import { Image, VStack,  Box,Text, HStack, Flex, Center } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {

    const voteAverage=Math.round(item.vote_average)
    console.log(voteAverage)
    const colorGrade= voteAverage>6? '#0af549': "#d90910"

  return (

    <VStack 
        data-group position={'relative'} 
        overflow={'hidden'} 
        borderWidth={'10px'} 
        borderRadius={"2xl"} 
        bg={"#8861c2"}   
        alignContent={'center'}>
             <Box  display='flex' mt='2' alignItems='center' position={'absolute'}>
            {Array(5)
                .fill('')
                .map((_, i) => (
              <StarIcon
                boxSize={'6'}
                key={i}
                color={i < voteAverage/2 ? 'gold' : 'gray.300'}
                />
            ))}/</Box>
            <Image style={{marginTop:"0px"}}  w={'100%'} h={'100%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}></Image>
            <Text 
            fontSize={'3xl'}
            bg={"rgba(255, 255, 255, 0.832)"}
            color="black"
            transform={'auto'}
            translateY="100%" 
            position={'absolute'} 
            bottom='0' 
            left='0' 
            right='0' 
            maxHeight={'100%'}
            overflow='scroll'
            _groupHover={{transform: "translateY(0%)", transition: "transform 0.3s ease-in-out"
            }}
            >{item.overview}
            </Text>
            <Center isTruncated
            px={'1rem'}
            textOverflow={'ellipsis'} 
            overflow="hidden" 
            height={'4.5em'} 
            fontSize="lg" 
            fontWeight={'bold'}
            >{item.original_title||item.original_name}
            </Center> 
    </VStack>

  )
}
