import React, { useState, useEffect } from "react";
import { Route, Routes } from "react-router-dom";
import Header from "./Header";
import Main from "./MainOld";
import SearchBar from "./SearchBar";

import Home from "./Home";
import Home2 from "./Home2";
import getTrending from "./utils/getTrending";
import DetailView from "./DetailView";
import getSearch from "./utils/getSearch";

function App() {
  const [movieData, setMovieData] = useState("");
  const [tvData, setTvData] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchData, setSearchData] = useState("");
  const getSearchValue = (value) => {
    setSearchTerm(value);
  };

  useEffect(() => {
    const getTrendingMovies = async () => {
      const trendingMovies = await getTrending("movie", "week");
      setMovieData(trendingMovies);
    };
    const getTrendingTV = async () => {
      const trendingTV = await getTrending("tv", "week");
      setTvData(trendingTV);
    };
    getTrendingMovies();
    getTrendingTV();
  }, []);

  useEffect(() => {
    const getSearchResults = async () => {
      const searchResults = await getSearch(searchTerm, 1);
      setSearchData(searchResults);
    };
    getSearchResults();
  }, []);

  console.log(searchData);
  console.log(tvData);
  console.log(movieData);
  return (
    <>
      <Header />
      <SearchBar getSearchValue={getSearchValue} />
      <Routes>
        <Route path="movies" element={<Home2 data={movieData} />}></Route>
        <Route path="series" element={<Home2 data={tvData} />}></Route>
        <Route path="/" element={<Home2 data={movieData} />}></Route>
        <Route path="*" element={<Home2 data={movieData} />}></Route>
        <Route path="/:mediatype/:id" element={<DetailView />}></Route>
        {/* <Route path="/search/:searchTerm" element={<Home2 data={}/>}></Route> */}
      </Routes>
    </>
  );
}

export default App;
