import React, { useState, useEffect } from "react";
import { Route, Routes, useLocation } from "react-router-dom";
import Header from "./Header";
import SearchBar from "./SearchBar";
import MainDisplay from "./MainDisplay";
import getTrending from "./utils/getTrending";
import DetailView from "./DetailView";
import getSearch from "./utils/getSearch";

function App() {
  const [movieData, setMovieData] = useState("");
  const [tvData, setTvData] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchData, setSearchData] = useState("");
  const [pageValue, setPageValue] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const mediatype = useLocation().pathname?.includes("movie") ? "movie" : "tv";

  const getSearchValue = (value) => {
    setSearchTerm(value);
  };

  const getPageValue = (value) => {
    setPageValue(value);
  };

  useEffect(() => {
    const getTrendingMovies = async () => {
      const trendingMovies = await getTrending("movie", "week");
      setMovieData(trendingMovies);
    };
    const getTrendingTV = async () => {
      const trendingTV = await getTrending("tv", "week");
      setTvData(trendingTV);
    };
    getTrendingMovies();
    getTrendingTV();
  }, []);

  useEffect(() => {
    const getSearchResults = async () => {
      const searchResults = await getSearch(mediatype, searchTerm, pageValue);
      setSearchData(await searchResults?.results);
      setTotalPages(await searchResults?.total_pages);
    };
    searchTerm ? getSearchResults() : setTvData();
  }, [searchTerm, pageValue]);

  return (
    <>
      <Header />
      <SearchBar getSearchValue={getSearchValue} getPageValue={getPageValue} />
      <Routes>
        <Route path="movies" element={<MainDisplay data={movieData} />}></Route>
        <Route path="tv" element={<MainDisplay data={tvData} />}></Route>
        <Route path="/" element={<MainDisplay data={movieData} />}></Route>
        <Route path="*" element={<MainDisplay data={movieData} />}></Route>
        <Route path="/:mediatype/:id" element={<DetailView />}></Route>
        <Route
          path="/search/:searchTerm"
          element={
            <MainDisplay
              data={searchData}
              getPageValue={getPageValue}
              pageValue={pageValue}
              totalPages={totalPages}
            />
          }
        ></Route>
      </Routes>
    </>
  );
}

export default App;
