import { Box,Image, VStack, Text } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {
    console.log(item.overview)
  return (

    <VStack data-group position={'relative'} overflow={'hidden'} borderWidth={'10px'} borderRadius={"2xl"} bg={"#8861c2"} alignContent={'center'}>
    <Image position={'ab'} w={'100%'} h={'90%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}></Image>
    <Text 
    transform={'auto'}
    translateY="100%" 
    position={'absolute'} 
    bottom='0' 
    left='0' 
    right='0' 
    maxHeight={'100%'}
    _groupHover={{color:"yellow",
        overflow: "auto"}}
    >{item.overview}
    </Text>
    <Text textOverflow={'clip'} overflow="hidden" height={'4.5em'} textAlign={'center'} fontSize="lg" fontWeight={'bold'}   >{item.original_title||item.original_name}</Text> 

    </VStack>

  )
}

