import { MoonIcon, SunIcon } from "@chakra-ui/icons";
import {
  Button,
  ButtonGroup,
  Flex,
  Spacer,
  useColorMode,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  Input,
} from "@chakra-ui/react";
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useInput } from "../src/utils/customHooks";

const Header = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const navigate = useNavigate();
  const handleHome = () => navigate("/");
  const email = useInput("email");
  const password = useInput("password");
  const modalLogin = useDisclosure();
  const modalRegister = useDisclosure();

  return (
    <Flex py={4}>
      <Button size="lg" onClick={handleHome}>
        My TMDB
      </Button>
      <Spacer />
      <ButtonGroup pl="5rem" size={"lg"} gap={8}>
        <Link to={`/movie`}>
          <Button>Movies</Button>
        </Link>
        <Link to={`/tv`}>
          <Button>TV</Button>{" "}
        </Link>
      </ButtonGroup>
      <Spacer />
      <ButtonGroup size={"lg"} gap={2}>
        <>
          <Button onClick={modalLogin.onOpen}>Log In</Button>

          <Modal isOpen={modalLogin.isOpen} onClose={modalLogin.onClose}>
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Log in</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <Input
                  size={"lg"}
                  type="text"
                  placeholder="email"
                  {...email}
                ></Input>
                <Input
                  size={"lg"}
                  type="password"
                  placeholder="password"
                  {...password}
                ></Input>
              </ModalBody>

              <ModalFooter>
                <Button colorScheme="blue" mr={3} onClick={onClose}>
                  Close
                </Button>
                <Button colorScheme="blue">Submit</Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
        </>
        <Button>Register</Button>
        <Button onClick={toggleColorMode}>
          {colorMode === "light" ? <MoonIcon /> : <SunIcon />}
        </Button>
      </ButtonGroup>
    </Flex>
  );
};

export default Header;
