import { HStack, Image, Text, Flex, VStack, Heading } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";
import popcorn from "./Popcorn.png";
import backgroundImage from "./background.jpg";

const Home = () => {
  const [movieData, setMovieData] = useState("");
  const [tvData, setTvData] = useState("");

  useEffect(() => {
    const getTrendingMovies = async () => {
      const trendingMovies = await getTrending("movie", "week");
      setMovieData(trendingMovies?.slice(0, 9));
    };
    const getTrendingTV = async () => {
      const trendingTV = await getTrending("tv", "week");
      setTvData(trendingTV?.slice(0, 9));
    };
    getTrendingMovies();
    getTrendingTV();
  }, []);

  return (
    <Flex
      direction={"row"}
      h={"50rem"}
      w={"100%"}
      mr={"10rem"}
      pt="4rem"
      backgroundImage={backgroundImage}
    >
      {movieData ? (
        <Carousel autoPlay={false}>
          {movieData?.map((item, i) => (
            <VStack>
              <Heading>Trending Movies</Heading>
              <HStack h={"35rem"} m="2rem">
                <Image
                  style={{ marginTop: "0px" }}
                  w={"20rem"}
                  h={"30rem"}
                  borderTopRadius="base"
                  src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
                  onError={(e) => (e.currentTarget.src = popcorn)}
                ></Image>
                <Center
                  fontSize={"xl"}
                  fontWeight="semibold"
                  w={"30rem"}
                  h="30rem"
                  verticalAlign={"center"}
                  bgImage="linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)) , url('Background.jpg')"
                >
                  {item.overview}
                </Center>
              </HStack>
            </VStack>
          ))}
        </Carousel>
      ) : (
        <div></div>
      )}
      {tvData ? (
        <Carousel autoPlay={false}>
          {tvData.map((item, i) => (
            <VStack>
              <Heading>Trending TV series</Heading>
              <HStack h={"35rem"} m="2rem">
                <Image
                  style={{ marginTop: "0px" }}
                  w={"40rem"}
                  h={"30rem"}
                  borderTopRadius="base"
                  src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
                  onError={(e) => (e.currentTarget.src = popcorn)}
                ></Image>
                <Text fontSize={"xl"} fontWeight="semibold">
                  {item.overview}
                </Text>
              </HStack>
            </VStack>
          ))}
        </Carousel>
      ) : (
        <div></div>
      )}
    </Flex>
  );
};

export default Home;
