import { Flex, Image, VStack, Text } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {

  return (
    <VStack borderRadius={"2xl"} bg={"grey"}>
    <Image p={3} w={'80%'} borderRadius='2xl' src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}></Image>
    <Text py={3} fontSize="2xl">{item.original_title||item.original_name}</Text> 
    </VStack>
  )
}
