import {  Image, VStack, Text } from '@chakra-ui/react'
import { transform } from 'framer-motion'
import React from 'react'

export const Card = ({item}) => {
    console.log(item)
  return (
    <VStack position={'relative'} overflow={'hidden'} borderWidth={'10px'} borderRadius={"2xl"} bg={"#8861c2"} alignContent={'center'}>
    <Image position={'ab'} w={'100%'} h={'90%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}></Image>



    <Text 
    style={{transform:"translateY(100%)", transition: "transform 0.3s ease-in-out",}} 
    position={'absolute'} 
    bottom='0' 
    left='0' 
    right='0' 
    transform={'translateY'}
    _hover={{transform: "translateY(0%)",  overflow: "auto"}}
    >{item.overview}
    </Text>



    <Text textOverflow={'clip'} overflow="hidden" height={'4.5em'} textAlign={'center'} fontSize="lg" fontWeight={'bold'}   >{item.original_title||item.original_name}</Text> 

    </VStack>
  )
}

