import React from 'react'
import { Card } from './Card';
import { Flex, Spacer, Box } from '@chakra-ui/react';

export const CardGrid = ({data}) => {
  return (
    <Box  mt={7}>
        <Spacer/>
        <Flex justifyContent={'space-evenly'} wrap={'wrap'}>{data.map((item)=><Card item={item}/>)}</Flex>
        <Spacer/>
    </Box>
  )
}
