import { StarIcon } from '@chakra-ui/icons'
import { Image, VStack, Text, HStack, Flex, Center } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {

    const voteAverage=Math.round(item.vote_average * 10) / 10
    console.log(voteAverage)
  return (

    <VStack 
        data-group position={'relative'} 
        overflow={'hidden'} 
        borderWidth={'10px'} 
        borderRadius={"2xl"} 
        bg={"#8861c2"}   
        alignContent={'center'}>
            <Flex width={'100%'} direction={'row-reverse'} position={'absolute'}>
                <StarIcon position={'absolute'} textAnchor='HOLA' boxSize={'14'} color={"#06c439"}/>
                <Text pl={""} pt={"1.2rem"} boxSize={"10"} fontWeight='extrabold' position={'relative'} color={'black'} >{voteAverage}</Text>
            </Flex>
            <Image style={{marginTop:"0px"}}  w={'100%'} h={'100%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}></Image>
            <Text 
            fontSize={'3xl'}
            bg={"rgba(255, 255, 255, 0.832)"}
            color="black"
            transform={'auto'}
            translateY="100%" 
            position={'absolute'} 
            bottom='0' 
            left='0' 
            right='0' 
            maxHeight={'100%'}
            overflow='scroll'
            _groupHover={{transform: "translateY(0%)", transition: "transform 0.3s ease-in-out"
            }}
            >{item.overview}
            </Text>
            <Center isTruncated
            px={'1rem'}
            textOverflow={'ellipsis'} 
            overflow="hidden" 
            height={'4.5em'} 
            fontSize="lg" 
            fontWeight={'bold'}
            >{item.original_title||item.original_name}
            </Center> 
    </VStack>

  )
}
