import { Card } from './Card';
import { Flex } from '@chakra-ui/react';

export const CardGrid = ({data}) => {
  return (
    <Flex justifyContent={'space-between'} p={3} wrap={'wrap'}>{data.map((item)=><Card item={item}/>)}</Flex>
  )
}
