import {
  Box,
  Image,
  Center,
  Text,
  HStack,
  VStack,
  Heading,
  Spacer,
  Grid,
  GridItem,
} from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import getItem from "./utils/getItem";

const DetailView = () => {
  const { mediatype, id } = useParams();
  const [details, setDetails] = useState({});

  useEffect(() => {
    const viewItem = async () => {
      const itemInfo = await getItem(mediatype, id);
      setDetails(itemInfo);
    };
    viewItem(mediatype, id);
  }, []);
  console.log(details);
  return (
    <Box bg={"white"}>
      <Grid
        px={"8vw"}
        py="8vh"
        w="100vw"
        h="60vh"
        templateRows="repeat(4, 1fr)"
        templateColumns="repeat(10, 1fr)"
        gap={4}
      >
        <GridItem rowSpan={4} colSpan={3} bg="blue">
          <Image
            h="100%"
            overflow={"clip"}
            src={`https://image.tmdb.org/t/p/original/${details.poster_path}`}
          ></Image>
        </GridItem>

        <GridItem colSpan={6} bg="red"></GridItem>
        <GridItem colSpan={6} bg="green"></GridItem>
        <GridItem colSpan={6} bg="yellow"></GridItem>
        <GridItem colSpan={6} bg="black"></GridItem>
      </Grid>
    </Box>
    // <Center>
    //   <HStack>

    //     <VStack>
    //       <Heading>{details.original_title || details.original_name}</Heading>
    //       <Spacer />
    //       <Text>{details.overview}</Text>
    //       <Spacer />
    //     </VStack>
    //   </HStack>
    // </Center>
  );
};

export default DetailView;
