import {
  Box,
  Image,
  Center,
  Text,
  HStack,
  VStack,
  Heading,
  Spacer,
  Grid,
  GridItem,
} from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import getItem from "./utils/getItem";

const DetailView = () => {
  const { mediatype, id } = useParams();
  const [details, setDetails] = useState({});

  useEffect(() => {
    const viewItem = async () => {
      const itemInfo = await getItem(mediatype, id);
      setDetails(itemInfo);
    };
    viewItem(mediatype, id);
  }, []);

  return (
    <Grid templateColumns="repeat(5, 1fr)" gap={4}>
      <GridItem colSpan={2} h="10" bg="tomato" />
      <GridItem colStart={4} colEnd={6} h="10" bg="papayawhip" />
    </Grid>
    // <Center>
    //   <HStack>
    //     <Image
    //       h={"50vh"}
    //       w="30vw"
    //       src={`https://image.tmdb.org/t/p/original/${details.poster_path}`}
    //     ></Image>
    //     <VStack>
    //       <Heading>{details.original_title || details.original_name}</Heading>
    //       <Spacer />
    //       <Text>{details.overview}</Text>
    //       <Spacer />
    //     </VStack>
    //   </HStack>
    // </Center>
  );
};

export default DetailView;
