import {  Image, VStack, Text } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {

  return (
    <VStack borderWidth={'10px'} borderRadius={"2xl"} bg={"#8861c2"} alignContent={'center'}>
    <Image  w={'100%'} h={'90%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}></Image>
    <Text textOverflow={'elipsis'} overflow="hidden" height={'4.5em'} textAlign={'center'} fontSize="lg" fontWeight={'bold'}   >{item.original_title||item.original_name}</Text> 
    </VStack>
  )
}
