import { background, extendTheme, Flex } from "@chakra-ui/react";
import { mode } from "@chakra-ui/theme-tools";

const config = {
  initialColorMode: "light",
  useSystemColorMode: false,
};
const theme = extendTheme({
  config,
  styles: {
    global: (props) => ({
      body: {
        bg: "url('https://previews.123rf.com/images/krekdm/krekdm1607/krekdm160700084/59591884-seamless-cinema-pattern-can-be-used-for-wallpaper-website-background-wrapping-paper-cinema-vector-br.jpg')"(
          props
        ),
      },
    }),
  },
});

export default theme;
