import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";
import popcorn from './Popcorn.png'


const Home=()=>{
const [data,setData]=useState('')


useEffect(() => {
    const getData=async()=>{
    const trendingMovies= await getTrending()
    setData(trendingMovies)}
    getData() 
}, [])

console.log(data)

return(
<>
    <Text>HOLA</Text>
    <div style={{ width: 600, height: 400, margin: "0 auto" }}>
    {data?<Carousel >
        {data?.map((item, i) => (
          <Image style={{marginTop:"0px"}}  w={'60%'} h={'100%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`} onError={(e) => (e.currentTarget.src = popcorn)}></Image>
        ))}
    </Carousel>:<p>NADA</p>}
    </div>
</>)


}



export default Home
