import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";
import popcorn from './Popcorn.png'


const Home=()=>{
const [data,setData]=useState('')


useEffect(() => {
    const getData=async()=>{
    const trendingMovies= await getTrending()
    setData(trendingMovies?.slice(0,9))}
    getData() 
}, [])

console.log(data)

return(  
    <Flex direction={'row'} pl='4rem'  h={'35rem'} w={'50rem'}>
    {data?<Carousel autoPlay={false}>
        {data?.map((item, i) => (
        <HStack h={'35rem'} >
          <Image style={{marginTop:"0px"}}  w={'40rem'} h={'25rem'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`} onError={(e) => (e.currentTarget.src = popcorn)}></Image>
        <Text>{item.overview}</Text>
        </HStack>
        ))}
    </Carousel>:<p>NADA</p>}
    {data?<Carousel autoPlay={false}>
        {data?.map((item, i) => (
        <HStack h={'35rem'} >
          <Image style={{marginTop:"0px"}}  w={'40rem'} h={'25rem'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/original/${item.poster_path}`} onError={(e) => (e.currentTarget.src = popcorn)}></Image>
        <Text>{item.overview}</Text>
        </HStack>
        ))}
    </Carousel>:<p>NADA</p>}
    </Flex>)


}



export default Home
