import {
  Center,
  Image,
  Flex,
  Text,
  VStack,
  Spacer,
  Heading,
} from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import getTrending from "./utils/getTrending";
import Carousel from "framer-motion-carousel";
import popcorn from "./Popcorn.png";
import { CardGrid } from "./CardGrid";
// import backgroundImage from "./background.jpg";

const Home2 = () => {
  const [movieData, setMovieData] = useState("");
  const [tvData, setTvData] = useState("");

  useEffect(() => {
    const getTrendingMovies = async () => {
      const trendingMovies = await getTrending("movie", "week");
      setMovieData(trendingMovies);
    };
    const getTrendingTV = async () => {
      const trendingTV = await getTrending("tv", "week");
      setTvData(trendingTV?.slice(0, 4));
    };
    getTrendingMovies();
    getTrendingTV();
  }, []);

  console.log(movieData);
  return (
    <Flex direction={"column"} bgGradient="linear(to-r, #01b4e4, pink.500)">
      <Heading as="h2" size={"md"} alignContent="flex-start">
        Welcome to my movie database!
      </Heading>
      <Text>Here you can browse your favorite movies and Tv series!</Text>

      <Flex
        direction={"row"}
        h={"55vh"}
        w={"100%"}
        my={"4vh"}
        bgImage="linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)) , url('background.jpg')"
        overflow={"hidden"}
        // backgroundImage={backgroundImage}
      >
        {movieData ? (
          <Carousel autoPlay={false}>
            {movieData?.slice(0, 5).map((item, i) => (
              <Flex h={"20vh"} w="100vw" mx={"2vw"}>
                <Image
                  w="45vw"
                  h={"55vh"}
                  objectFit="contain"
                  borderTopRadius="base"
                  src={`https://image.tmdb.org/t/p/original/${item.poster_path}`}
                  onError={(e) => (e.currentTarget.src = popcorn)}
                />
                <VStack w={"40vw"} h="45vh">
                  <Text fontSize={"5xl"} fontWeight="bold" pt={"3vw"}>
                    {item.original_title}
                  </Text>
                  <Spacer />
                  <Center fontSize={"xl"} fontWeight="semibold">
                    {item.overview}
                  </Center>
                  <Spacer />
                </VStack>
              </Flex>
            ))}
          </Carousel>
        ) : (
          <div></div>
        )}
      </Flex>
      <CardGrid data={movieData}></CardGrid>
    </Flex>
  );
};

export default Home2;
