import {
  Box,
  Image,
  Center,
  Text,
  HStack,
  VStack,
  Heading,
  Spacer,
  Grid,
  GridItem,
} from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import getItem from "./utils/getItem";
import YouTube from "react-youtube";

const DetailView = () => {
  const { mediatype, id } = useParams();
  const [details, setDetails] = useState({});
  const trailer = details.videos?.results.find(
    (video) => video.name === "Official Trailer"
  );

  useEffect(() => {
    const viewItem = async () => {
      const itemInfo = await getItem(mediatype, id);
      setDetails(itemInfo);
    };
    viewItem(mediatype, id);
  }, []);

  return (
    <Box
      bgImage={`https://image.tmdb.org/t/p/w780/${details.images?.backdrops[0].file_path}`}
      bgSize="cover"
    >
      <YouTube
        videoId={`${trailer.key}`}
        opts={{ width: "100%", height: "200%" }}
      ></YouTube>

      <Heading
        textAlign={"center"}
        bgImage="linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)) , url('background.jpg')"
      >
        {details.original_title || details.original_name}
      </Heading>
      <Grid
        px={"8vw"}
        py="3vh"
        w="100vw"
        h="60vh"
        templateRows="repeat(8, 1fr)"
        templateColumns="repeat(10, 1fr)"
        bgImage="linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)) , url('background.jpg')"
      >
        <GridItem rowSpan={8} colSpan={3}>
          <Image
            px="10%"
            h="80%"
            w="80%"
            overflow={"clip"}
            src={`https://image.tmdb.org/t/p/original/${details.poster_path}`}
          ></Image>
        </GridItem>
        <GridItem colSpan={6}></GridItem>
        <GridItem colSpan={6}>
          <Text fontSize={"3xl"}>{details.overview}</Text>
        </GridItem>{" "}
        <GridItem colSpan={6}></GridItem>
        <GridItem colSpan={6}>
          <Text fontSize={"2xl"}>Release date: {details.release_date}</Text>
          <Text fontSize={"2xl"}>
            Vote: {details.vote_average} ({details.vote_count} votes)
          </Text>
        </GridItem>
        <GridItem colSpan={6}></GridItem>
      </Grid>
    </Box>
  );
};

export default DetailView;
