import { As, BoxProps } from '@chakra-ui/react';
import React, { Dispatch, ReactElement, SetStateAction } from 'react';

export const defaultData = {
  current: null, //*
  defaultCurrent: 1, //*
  defaultPage: 1, //*
  defaultPageSize: 10, //*
  disabled: false, //*
  hideOnSinglePage: false, //*
  itemRender: () => null, //*
  pageSize: 0, //*
  pageNeighbours: 0, //* indicates the number of additional page numbers to show on each side of the current page.
  pageSizeOptions: [10, 20, 50, 100], //*
  paginationProps: null, //*
  responsive: false, //*
  showQuickJumper: false, //*
  showSizeChanger: null, //*
  showTotal: () => null, //*
  simple: null, //*
  total: 0, //*
  onChange: () => '', //*
  onShowSizeChange: () => '', //*
  size: 'md', //*
  rounded: 'md', //*
  baseStyles: null, //*
  activeStyles: null, //*
  hoverStyles: null, //*
  colorScheme: 'brand', //*
  focusRing: false, //*
  basePath: null, //*
};

export type DefaultData = typeof defaultData;

export type Responsives =
  | 'activePage'
  | 'totalRender'
  | 'fastBackward'
  | 'fastForward'
  | 'pageSize'
  | 'pageJumper';

export interface Pagination {
  current: number | null; //*
  defaultCurrent: number; //*
  defaultPage: number; //*
  defaultPageSize: number; //*
  disabled: boolean; //*
  hideOnSinglePage: boolean; //*
  itemRender: (
    currentPage?: Pagination['currentPage'],
    type?: string
  ) => As<any> | any;
  pageSize: number; //*
  pageNeighbours: number; //* indicates the number of additional page numbers to show on each side of the current page.
  pageSizeOptions: number[]; //*
  paginationProps: BoxProps | null; //*
  responsive: Partial<Record<Responsives, boolean>> | boolean;
  showQuickJumper: boolean; //*
  showSizeChanger: boolean | null; //*
  showTotal: (total?: number) => ReactElement | any;
  simple: boolean | null; //*
  total: number; //*
  onChange: (
    currentPage?: Pagination['currentPage'],
    totalPages?: number,
    pageSize?: Pagination['pageSize'],
    total?: number
  ) => void;
  onShowSizeChange: (
    currentPage?: Pagination['currentPage'],
    size?: number
  ) => void;
  size: string; //*
  rounded: string; //*
  colorScheme: string; //*
  baseStyles: BoxProps | null;
  activeStyles: BoxProps | null;
  hoverStyles: BoxProps | null;
  currentPage: number;
  setCurrentPage: Dispatch<SetStateAction<Pagination['currentPage']>>;
  setPageSize: Dispatch<SetStateAction<Pagination['pageSize']>>;
  focusRing: boolean | string;
  basePath: string | null;
}

const PaginationContext = React.createContext({} as Pagination);

export default PaginationContext;
import React from 'react';

import { BoxProps, useColorModeValue } from '@chakra-ui/react';
import PagContext from '../providers/pagination-provider';

export const usePaginationStyles = (props: any) => {
  const {
    colorScheme,
    rounded,
    size,
    baseStyles,
    activeStyles,
    hoverStyles,
  } = React.useContext(PagContext);

  const activeStyle: BoxProps = activeStyles || {
    bg: useColorModeValue(`${colorScheme}.600`, `${colorScheme}.500`),
    color: useColorModeValue('white', 'gray.200'),
    cursor: 'pointer',
  };

  const hoverStyle: BoxProps = {
    _hover: hoverStyles || activeStyle,
  };
  const baseStyle: BoxProps = baseStyles || {
    rounded: rounded,
    bg: useColorModeValue('white', 'gray.800'),
    color: useColorModeValue('gray.700', 'gray.200'),
    userSelect: 'none',
  };
  const getSizeStyle = (size: string) => {
    let styles: BoxProps = {};
    switch (size) {
      case 'xs':
        styles = { px: 2, fontSize: 'xs' };
        break;

      case 'sm':
        styles = { px: 3, py: 1, fontSize: 'sm' };
        break;

      case 'md':
        styles = { px: 4, py: 1, fontSize: 'md' };
        break;

      case 'lg':
        styles = { px: 5, py: 2, fontSize: 'lg' };
        break;

      default:
        break;
    }
    return styles;
  };
  const sizeStyle = getSizeStyle(size);
  const disabledStyle: BoxProps = {
    opacity: 0.6,
    cursor: 'not-allowed',
  };
  return {
    ...sizeStyle,
    ...baseStyle,
    ...(!props.disabled && hoverStyle),
    ...(props.active && activeStyle),
    ...(props.disabled && disabledStyle),
  };
};
import React from 'react';

import { Button } from '@chakra-ui/react';
import { usePaginationStyles } from '../services/styles';
import PagContext from '../providers/pagination-provider';

const handleResponsive = (props: any) => {
  const { type, active, responsive } = props;
  let show = true;
  switch (type) {
    case 'page':
      if (!active) show = false;
      else if (!responsive.activePage) show = false;
      break;

    default:
      break;
  }
  return !show && 'none';
};

export const PagButton = (props: any) => {
  const { responsive, focusRing } = React.useContext(PagContext);
  const paginationStyles: any = usePaginationStyles(props);
  const display: any = {
    display: [
      responsive && handleResponsive({ ...props, responsive }),
      'block',
    ],
  };
  const onClick: any = !props.disabled ? props.onClick : null;
  const href: any = props.href ? {href: props.href} : {}

  const focusRingStyles = {
    boxShadow: focusRing
      ? typeof focusRing === 'string'
        ? `0 0 0 3px ${focusRing}`
        : `0 0 0 3px rgba(66, 153, 225, 0.6)`
      : 'none',
  };
  return (
    <Button
      onClick={onClick}
      as={props.as}
      mx={1}
      my="auto"
      {...paginationStyles}
      _first={{ ml: 0 }}
      _last={{ mr: 0 }}
      _focus={focusRingStyles}
      size={props.size}
      {...display}
      {...href}
    >
      {props.children}
    </Button>
  );
};
import React from 'react';
import PagContext from '../providers/pagination-provider';

const LEFT_PAGE = 'LEFT';

const RIGHT_PAGE = 'RIGHT';

/**
 * Helper method for creating a range of numbers
 * range(1, 5) => [1, 2, 3, 4, 5]
 */
const range = (from: number, to: number, step = 1) => {
  let i = from;
  const range = [];

  while (i <= to) {
    range.push(i);
    i += step;
  }

  return range;
};

export const fetchPageNumbers = () => {
  const props = React.useContext(PagContext);
  const totalPages: any = Math.ceil(props.total / props.pageSize);
  const currentPage = props.currentPage;
  const pageNeighbours = props.pageNeighbours;

  /**
   * totalNumbers: the total page numbers to show on the control
   * totalBlocks: totalNumbers + 2 to cover for the left(<) and right(>) controls
   */
  const totalNumbers = pageNeighbours * 2 + 3;
  const totalBlocks = totalNumbers + 2;

  if (totalPages > totalBlocks) {
    const startPage = Math.max(2, currentPage - pageNeighbours);
    const endPage = Math.min(totalPages - 1, currentPage + pageNeighbours);
    let pages: (string | number)[] = range(startPage, endPage);

    /**
     * hasLeftSpill: has hidden pages to the left
     * hasRightSpill: has hidden pages to the right
     * spillOffset: number of hidden pages either to the left or to the right
     */
    const hasLeftSpill = startPage > 2;
    const hasRightSpill = totalPages - endPage > 1;
    const spillOffset = totalNumbers - (pages.length + 1);

    switch (true) {
      // handle: (1) < {5 6} [7] {8 9} (10)
      case hasLeftSpill && !hasRightSpill: {
        const extraPages = range(startPage - spillOffset, startPage - 1);
        pages = [LEFT_PAGE, ...extraPages, ...pages];
        break;
      }

      // handle: (1) {2 3} [4] {5 6} > (10)
      case !hasLeftSpill && hasRightSpill: {
        const extraPages = range(endPage + 1, endPage + spillOffset);
        pages = [...pages, ...extraPages, RIGHT_PAGE];
        break;
      }

      // handle: (1) < {4 5} [6] {7 8} > (10)
      case hasLeftSpill && hasRightSpill:
      default: {
