          >
            / 5
          </chakra.span>
        </React.Fragment>
      ) : (
        <React.Fragment>
          <PagButton
            as={prevRender}
            disabled={props.currentPage === 1 || props.disabled}
            onClick={backward}
            {...hrefLink(props.currentPage - 1)}
            size={
              props.size === 'lg' ? 'md' : props.size === 'xs' ? 'xs' : 'sm'
            }
          >
            {!prevRender && '<'}
          </PagButton>

          {pages.map((page, index) => {
            if (page === 'LEFT')
              return (
                <PagButton
