                  as={rightRender}
                  key={index}
                  onClick={fastForward}
                  {...hrefLink(props.currentPage + props.pageNeighbours * 2 + 1)}
                  size={
                    props.size === 'lg'
                      ? 'md'
                      : props.size === 'xs'
                      ? 'xs'
                      : 'sm'
                  }
                  {...responsiveDisplay('fastForward')}
                  my="auto"
                >
                  {!rightRender && '>>'}
                </PagButton>
              );
            const active = page === props.currentPage;
            return (
              <PagButton
                as={pageRender}
                disabled={props.disabled}
                active={active}
                key={`page-${index}`}
                onClick={(evt: any) => gotoPage(evt, page)}
                {...hrefLink(page)}
                size={
                  props.size === 'lg' ? 'md' : props.size === 'xs' ? 'xs' : 'sm'
                }
                type="page"
              >
                {page}
              </PagButton>
            );
          })}
          <PagButton
            as={nextRender}
            disabled={props.currentPage === totalPages || props.disabled}
            onClick={forward}
            {...hrefLink(props.currentPage + 1)}
            size={
              props.size === 'lg' ? 'md' : props.size === 'xs' ? 'xs' : 'sm'
            }
          >
            {!nextRender && '>'}
          </PagButton>
        </React.Fragment>
      )}
      {props.showSizeChanger && (
        <Menu>
          <PagMenuButton
            ml={1}
            as={pageSizeRender}
            disabled={props.disabled}
            size={
              props.size === 'lg' ? 'md' : props.size === 'xs' ? 'xs' : 'sm'
            }
          >
            {props.pageSize} / page
          </PagMenuButton>
          <MenuList>
            <MenuOptionGroup onChange={changePageSize}>
              {props.pageSizeOptions
                .filter((opt: any) => opt !== props.pageSize)
                .map((opt: any, oid: number) => (
                  <MenuItemOption
                    fontSize={props.size}
                    value={opt.toString()}
                    key={`size${oid}`}
                  >
                    {opt} / page
                  </MenuItemOption>
                ))}
            </MenuOptionGroup>
          </MenuList>
        </Menu>
      )}
      {props.showQuickJumper && (
        <HStack
          as="form"
          userSelect="none"
          opacity={props.disabled ? 0.6 : 1}
          ml={2}
          onSubmit={pageJumperUpdate}
          {...responsiveDisplay('pageJumper')}
        >
          <Text wordBreak="unset">Go to:</Text>
          <Input
            width="50px"
            value={pageJumper}
            isDisabled={props.disabled}
            onChange={e => setPageJumper(e.target.value)}
            size={
              props.size === 'lg' ? 'md' : props.size === 'xs' ? 'xs' : 'sm'
            }
          />
        </HStack>
      )}
    </Box>
  ) : null;
};

