import React from 'react'
import { Card } from './Card';
import { Flex, Spacer, Box, space } from '@chakra-ui/react';

export const CardGrid = ({data}) => {
  return (
    <Box p={7,7}  mt={7}>
        <Flex justifyContent={'space-evenly'} wrap={'wrap'}>{data.map((item)=><Card item={item}/>)}</Flex>

    </Box>
  )
}
