import { SearchIcon } from "@chakra-ui/icons";
import {Box, Text, Input, InputGroup, InputRightElement } from "@chakra-ui/react";

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";



 const SearchBar = ({getSearchValue}) => {
    const navigate=useNavigate()
    const [value, setValue] = useState('')
    const handleChange = (e) => setValue(e.target.value)
    const handleSubmit= (e) => {getSearchValue(value); setValue(''); navigate(`/search/${value}`) }



    return (
      <Box pb='1rem'>

        <InputGroup width={'30%'}>
        <Input
        bg={'white'}
          value={value}
          onChange={handleChange}
          placeholder='Here is a sample placeholder'
          size='md'
          borderColor={"black"}
          borderWidth={'2px'}
          onKeyDown={(e)=>{if (e.key==='Enter') handleSubmit()}}
        />
        <InputRightElement children={<SearchIcon onClick={handleSubmit}/>}/>
        </InputGroup>
      </Box>
    )
  }
 export default SearchBar
