import { StarIcon } from '@chakra-ui/icons'
import { Image, VStack, Text, HStack } from '@chakra-ui/react'
import React from 'react'

export const Card = ({item}) => {
    console.log(item)
  return (

    <VStack data-group position={'relative'} overflow={'hidden'} borderWidth={'10px'} borderRadius={"2xl"} bg={"#8861c2"} alignContent={'center'}>
        <HStack position={'absolute'}>  
            <Text color={'yellow'} >HOLA</Text>
            <StarIcon/>
        </HStack>
        <Image style={{marginTop:"0px"}}  w={'100%'} h={'100%'} borderTopRadius='base' src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}></Image>
        <Text 
        bg={"rgba(255, 255, 255, 0.832)"}
        color="black"
        transform={'auto'}
        translateY="100%" 
        position={'absolute'} 
        bottom='0' 
        left='0' 
        right='0' 
        maxHeight={'100%'}
        _groupHover={{transform: "translateY(0%)", transition: "transform 0.3s ease-in-out"
        }}
        >{item.overview}
        </Text>
        <Text 
        textOverflow={'clip'} 
        overflow="hidden" 
        height={'4.5em'} 
        textAlign={'center'} 
        fontSize="lg" 
        fontWeight={'bold'}>{item.original_title||item.original_name}</Text> 

    </VStack>

  )
}
