import { Flex } from '@chakra-ui/react';
import axios from 'axios';
import React, { useEffect, useState } from 'react'




const Main = ({searchTerm}) => {
    const tmdbKey = "5f4cf67aa6b81c56e16cff977537cf17";
    const basicURL= "https://api.themoviedb.org/3/search/";

    const [trending,setTrending]= useState([])

    useEffect(() => {
        const fetchData= async ()=>{ 
        const axiosRequest= await axios.get("https://api.themoviedb.org/3/trending/all/day?api_key=5f4cf67aa6b81c56e16cff977537cf17")
        setTrending(axiosRequest.data.results)}
        fetchData()     
    }, [])

    useEffect(() => {
        const fetchData= async ()=>{ 
        const axiosRequest= await axios.get(`${basicURL}movie?page=1&query=${searchTerm}&api_key=${tmdbKey}`)
        setTrending(axiosRequest.data.results)}
        fetchData()     
    }, [searchTerm])




    //     axios
    //    .get(`${basicURL}${searchType}?page=${value}&query=${searchTerm}&api_key=${tmdbKey}`)
    //    .then((res) => res.data)
    //   .then((result) => {saveSearch(result); setActiveSearch(true)})}

  return (

    <Flex wrap={'wrap'}>{trending.map((item)=><Card item={item}/>)}</Flex>
  )
}

export default Main
