import { SearchIcon } from "@chakra-ui/icons";
import { Text, Input, InputGroup, InputRightElement } from "@chakra-ui/react";

import React, { useState } from "react";



 const SearchBar = ({getSearchValue}) => {

    const [value, setValue] = useState('')
    const handleChange = (e) => setValue(e.target.value)
    const handleSubmit= (e) => {e.preventDefault(); getSearchValue(value); setValue('')}



    return (
      <>
        <Text  mb='8px'>Value: {value}</Text>
        <InputGroup width={'30%'}>
        <Input
          value={value}
          onChange={handleChange}
          placeholder='Here is a sample placeholder'
          size='md'
          borderColor={"black"}
          borderWidth={'2px'}
        />
        <InputRightElement children={<SearchIcon onClick={handleSubmit}/>}/>
        </InputGroup>
      </>
    )
  }
 export default SearchBar
