import { Flex, Spinner, Box } from "@chakra-ui/react";
import Pagination from "@choc-ui/paginator";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { CardGrid } from "./CardGrid";
import getSearch from "./utils/getSearch";
import getTrending from "./utils/getTrending";
import backgroundImage from "./background";

const Main = ({ searchTerm }) => {
  const [totalPages, setTotalPages] = useState(false);
  const [data, setData] = useState([]);
  const [pageValue, setPageValue] = useState(1);
  const getPageValue = (value) => setPageValue(value);

  //   useEffect(() => {
  //     const getData = async () => {
  //       const trendingMovies = await getTrending();
  //       setData(trendingMovies);
  //     };
  //     getData();
  //   }, []);

  useEffect(() => {
    const fetchData = async () => {
      const getSearchData = await getSearch({ searchTerm, pageValue });
      console.log(getSearchData);
      return getSearchData
        ? (setTotalPages(getSearchData?.total_pages),
          setData(getSearchData?.results))
        : totalPages;
    };
    fetchData();
  }, [searchTerm, pageValue]);

  const handlePagechange = (value) => {
    setPageValue(value);
    window.scrollTo(0, 0);
  };

  return (
    <Box backgroundImage="">
      <CardGrid data={data} />
      <Flex
        w="full"
        bg={"gray.400"}
        _dark={{
          bg: "gray.600",
        }}
        p={50}
        alignItems="center"
        justifyContent="center"
      >
        <Pagination
          defaultCurrent={1}
          total={totalPages * 10}
          onChange={handlePagechange}
          pageNeighbours={2}
          paginationProps={{
            display: "flex",
          }}
        />
      </Flex>
      ;
    </Box>
  );
};

export default Main;
